import React from 'react';
import './Modal.css';

const Modal = ({ isOpen, onClose, profile }) => {
  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Mon profil GitHub</h2>
        </div>
        <div className="modal-body">
          <img 
            src={profile.avatar_url} 
            alt="Avatar"
          />
          <p>{profile.name || "Nicolas.B"}</p>
          <p>{profile.bio || "As we all know, John Doe's identity is unknown. I just wanted to contribute without being known."}</p>
          <div className="github-stats">
            <p>Repos: {profile.public_repos || "1"}</p>
            <p>Followers: {profile.followers || "16"}</p>
            <p>Following: {profile.following || "0"}</p>
          </div>
        </div>
        <div className="modal-footer">
          <button className="close-button" onClick={onClose}>Fermer</button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
