import './App.css';
import React, { useState, useEffect } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import Navbar from './components/Navbar';
import HeroSection from "./components/HeroSection";
import AboutAndSkills from "./components/AboutAndSkills";
import Footer from "./components/Footer";
import Modal from './components/Modal';
import "./index.css";


function App() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [profile, setProfile] = useState({});

  useEffect(() => {
    fetch('https://api.github.com/users/Devco01')
      .then(response => {
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération du profil GitHub');
        }
        return response.json();
      })
      .then(data => {
        console.log('Données GitHub reçues:', data); // Pour déboguer
        setProfile(data);
      })
      .catch(error => {
        console.error('Erreur:', error);
      });
  }, []);

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="App">
      <Navbar />
      <HeroSection onButtonClick={handleOpenModal} />
      <AboutAndSkills />
      <Footer />
      <Modal isOpen={isModalOpen} onClose={handleCloseModal} profile={profile} />
    </div>
  );
}

export default App;
