import './App.css';
import React, { useState, useEffect } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import Header from './components/Header';
import Home from './components/Home/Home';
import Footer from "./components/Footer";
import Modal from './components/Modal';
import "./index.css";

function App() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [profile, setProfile] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch('https://api.github.com/users/Devco01', {
      headers: {
        'Authorization': `token ${process.env.REACT_APP_GITHUB_TOKEN}`,
        'Accept': 'application/vnd.github.v3+json'
      }
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`GitHub API error: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Données GitHub reçues:', data);
        setProfile(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Erreur:', error);
        setIsLoading(false);
      });
  }, []);

  const handleOpenModal = () => {
    if (profile) {
      console.log('Ouverture de la modale avec le profil:', profile);
      setIsModalOpen(true);
    } else {
      console.log('Profil non disponible');
    }
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="App">
      <Header />
      <Home onButtonClick={handleOpenModal} />
      <Footer />
      {!isLoading && profile && (
        <Modal 
          isOpen={isModalOpen} 
          onClose={handleCloseModal} 
          profile={profile} 
        />
      )}
    </div>
  );
}

export default App;
