import React, { useEffect, useState } from 'react';
import { createPortal } from 'react-dom';
import './Modal.css';
import userIcon from '../images/utilisateur.png';
import cubeIcon from '../images/cube.png';
import paragraphIcon from '../images/paragraphe.png';

const Modal = ({ isOpen, onClose, profile }) => {
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
    return () => setMounted(false);
  }, []);

  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isOpen]);

  if (!mounted || !isOpen) return null;

  const modalContent = (
    <div className="modal-overlay" onClick={(e) => {
      if (e.target === e.currentTarget) onClose();
    }}>
      <div className="modal">
        <div className="modal-header">
          <h2>Mon profil GitHub</h2>
          <button className="close-icon" onClick={onClose}>
            <svg width="16" height="16" viewBox="0 0 16 16" fill="currentColor">
              <path d="M3.72 3.72a.75.75 0 011.06 0L8 6.94l3.22-3.22a.75.75 0 111.06 1.06L9.06 8l3.22 3.22a.75.75 0 11-1.06 1.06L8 9.06l-3.22 3.22a.75.75 0 01-1.06-1.06L6.94 8 3.72 4.78a.75.75 0 010-1.06z"/>
            </svg>
          </button>
        </div>
        <div className="modal-body">
          <div className="modal-avatar">
            {profile?.avatar_url ? (
              <img 
                src={profile.avatar_url}
                alt="Avatar"
                onError={(e) => {
                  console.log('Erreur de chargement de l\'image');
                  e.target.src = "https://github.com/identicons/Devco01.png";
                }}
              />
            ) : (
              <img 
                src="https://github.com/identicons/Devco01.png"
                alt="Avatar par défaut"
              />
            )}
          </div>
          <div className="modal-info">
            <a href={profile?.html_url} target="_blank" rel="noopener noreferrer" className="github-link">
              <img src={userIcon} alt="User" className="icon" /> {profile?.login || "Devco01"}
            </a>
            <div className="github-stats">
              <p>
                <img src={cubeIcon} alt="Repositories" className="icon" /> Repositories : {profile?.public_repos || "0"}
              </p>
              <p>
                <img src={paragraphIcon} alt="Bio" className="icon" /> Bio : {profile?.bio || "Développeur web full-stack"}
              </p>
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button className="close-button" onClick={onClose}>Fermer</button>
        </div>
      </div>
    </div>
  );

  return createPortal(modalContent, document.body);
};

export default Modal;
