import React from 'react';
import { createPortal } from 'react-dom';
import './Modal.css';

const Modal = ({ isOpen, onClose, profile }) => {
  if (!isOpen) return null;

  const modalContent = (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>Mon profil GitHub</h2>
          <button className="close-icon" onClick={onClose}>√ó</button>
        </div>
        <div className="modal-body">
          <div className="modal-avatar">
            {profile?.avatar_url ? (
              <img 
                src={profile.avatar_url}
                alt="Avatar"
                onError={(e) => {
                  e.target.src = "https://github.com/identicons/Devco01.png";
                }}
              />
            ) : (
              <img 
                src="https://github.com/identicons/Devco01.png"
                alt="Avatar par d√©faut"
              />
            )}
          </div>
          <div className="modal-info">
            <div className="info-item username">
              <span className="icon">üë§</span>
              {profile?.login || "Devco01"}
            </div>
            <div className="info-item location">
              <span className="icon">üìç</span>
              {profile?.location || "Non sp√©cifi√©"}
            </div>
            <div className="info-item bio">
              <span className="icon">üìù</span>
              {profile?.bio || "As we all know, John Doe's identity is unknown. I just wanted to contribute without being known."}
            </div>
            <div className="info-item">
              <span className="icon">üìö</span>
              Repositories : {profile?.public_repos || "1"}
            </div>
            <div className="info-item">
              <span className="icon">üë•</span>
              Followers : {profile?.followers || "16"}
            </div>
            <div className="info-item">
              <span className="icon">üë§</span>
              Following : {profile?.following || "0"}
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button className="close-button" onClick={onClose}>Fermer</button>
        </div>
      </div>
    </div>
  );

  return createPortal(modalContent, document.body);
};

export default Modal;
